import React, { useState } from 'react';

const Onsubmit=()=>{
    const[data,setData]=useState();
    const[username,password]=data;

    const handler=e=>{
     setData({...data,[e.target.name]:[e.target.value]})
    }
return(
    <div>
        <h1>Onsubmit</h1>
        <form>
        <input type="text" placeholder='enter name'name='username' value={username} onChange={handler} />
        <input type='password' placeholder='enter password' name='password' value={password} onChange={handler} />
        <input type='submit' />
        <br/>
        </form>
    </div>
)
}
export default Onsubmit
